/* Custom styles for Leaflet and other elements that are easier to manage here than with Tailwind utilities */

body {
    background-image: url('https://www.transparenttextures.com/patterns/subtle-leaves.png');
}

#map {
    z-index: 10;
}

/* Customizing the routing line */
.leaflet-routing-container .leaflet-routing-alt {
    display: none; /* Hides the instruction box */
}

.leaflet-routing-line {
    stroke: #87CEEB !important; /* sky-blue */
    stroke-width: 5 !important;
    stroke-opacity: 0.8 !important;
}

/* Custom scrollbar for a more modern look */
::-webkit-scrollbar {
    width: 8px;
}

::-webkit-scrollbar-track {
    background: #f1f1f1;
    border-radius: 10px;
}

::-webkit-scrollbar-thumb {
    background: #4A6C3A; /* nature-green */
    border-radius: 10px;
}

::-webkit-scrollbar-thumb:hover {
    background: #6B9057; /* nature-green-light */
}

.leaflet-popup-content-wrapper {
    border-radius: 8px;
    box-shadow: 0 4px 6px rgba(0,0,0,0.1);
}

.leaflet-popup-content {
    margin: 15px !important;
}

.leaflet-popup-tip {
    background: white;
}

.location-popup h3 {
    margin: 0 0 5px 0;
    font-size: 1.2rem;
    font-weight: bold;
    color: #4A6C3A;
}

.location-popup p {
    margin: 0;
    font-size: 0.9rem;
    color: #333333;
}

document.addEventListener('DOMContentLoaded', () => {

    // --- DATA --- //
    const locations = [
        { name: 'Yosemite National Park', coords: [37.8651, -119.5383], description: 'A spectacular granite-cliffed valley in California.' },
        { name: 'Yellowstone National Park', coords: [44.4280, -110.5885], description: 'Home to geysers and abundant wildlife.' },
        { name: 'Grand Canyon National Park', coords: [36.1069, -112.1129], description: 'A mile-deep gorge carved by the Colorado River.' },
        { name: 'Zion National Park', coords: [37.2982, -113.0263], description: 'Utah\'s premier park with massive sandstone cliffs.' },
        { name: 'Banff National Park', coords: [51.4968, -115.9281], description: 'Canada\'s oldest national park, in the Rocky Mountains.' },
        { name: 'Torres del Paine', coords: [-50.9423, -72.9654], description: 'A stunning national park in Chilean Patagonia.' },
        { name: 'Fiordland National Park', coords: [-45.4145, 167.7155], description: 'Home to the dramatic Milford Sound in New Zealand.' }
    ];

    // --- MAP INITIALIZATION --- //
    const map = L.map('map').setView([40, -98], 4); // Centered on the US

    L.tileLayer('https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>',
        subdomains: 'abcd',
        maxZoom: 20
    }).addTo(map);

    // --- UI ELEMENTS --- //
    const locationsList = document.getElementById('locations-list');
    const startPointSelect = document.getElementById('start-point');
    const endPointSelect = document.getElementById('end-point');
    const findRouteBtn = document.getElementById('find-route-btn');
    const mobileMenuButton = document.getElementById('mobile-menu-button');
    const mobileMenu = document.getElementById('mobile-menu');

    let routeControl = null;

    // --- FUNCTIONS --- //

    // Populate UI elements with location data
    const populateUI = () => {
        locations.forEach((location, index) => {
            // Populate sidebar list
            const locationEl = document.createElement('div');
            locationEl.innerHTML = `<i class="fas fa-map-marker-alt text-nature-green-light mr-2"></i> ${location.name}`;
            locationEl.className = 'p-2 rounded-md hover:bg-gray-100 cursor-pointer transition-colors';
            locationEl.addEventListener('click', () => {
                map.flyTo(location.coords, 10);
                // Find the corresponding marker and open its popup
                map.eachLayer(layer => {
                    if (layer instanceof L.Marker && layer.getLatLng().equals(L.latLng(location.coords))) {
                        layer.openPopup();
                    }
                });
            });
            locationsList.appendChild(locationEl);

            // Populate dropdowns
            const option = document.createElement('option');
            option.value = index;
            option.textContent = location.name;
            startPointSelect.appendChild(option.cloneNode(true));
            endPointSelect.appendChild(option.cloneNode(true));
        });
        endPointSelect.selectedIndex = 1; // Default to a different end point
    };

    // Add markers to the map
    const addMarkers = () => {
        locations.forEach(location => {
            const customIcon = L.divIcon({
                html: '<i class="fas fa-tree text-2xl text-nature-green"></i>',
                className: 'custom-map-icon',
                iconSize: [30, 42],
                iconAnchor: [15, 42]
            });

            const marker = L.marker(location.coords, { icon: customIcon }).addTo(map);

            const popupContent = `
                <div class="location-popup">
                    <h3>${location.name}</h3>
                    <p>${location.description}</p>
                </div>
            `;
            marker.bindPopup(popupContent);
        });
    };

    // Find and display a route
    const findRoute = () => {
        if (routeControl) {
            map.removeControl(routeControl);
        }

        const startLocation = locations[startPointSelect.value];
        const endLocation = locations[endPointSelect.value];

        if (startLocation.name === endLocation.name) {
            alert('Please select two different locations for the route.');
            return;
        }

        routeControl = L.Routing.control({
            waypoints: [
                L.latLng(startLocation.coords),
                L.latLng(endLocation.coords)
            ],
            routeWhileDragging: false,
            show: false, // Hides the default instructions panel
            addWaypoints: false,
            createMarker: () => null // Prevent default markers
        }).addTo(map);
    };

    // Toggle mobile menu
    const toggleMobileMenu = () => {
        mobileMenu.classList.toggle('hidden');
    };

    // --- EVENT LISTENERS --- //
    findRouteBtn.addEventListener('click', findRoute);
    mobileMenuButton.addEventListener('click', toggleMobileMenu);

    // --- INITIALIZATION CALLS --- //
    populateUI();
    addMarkers();
});

document.addEventListener('DOMContentLoaded', () => {
    const galleryGrid = document.getElementById('gallery-grid');
    const lightbox = document.getElementById('lightbox');
    const lightboxImg = document.getElementById('lightbox-img');
    const lightboxClose = document.getElementById('lightbox-close');
    const mobileMenuButton = document.getElementById('mobile-menu-button');
    const mobileMenu = document.getElementById('mobile-menu');

    const images = [
        { src: 'https://images.unsplash.com/photo-1470071459604-3b5ec3a7fe05?q=80&w=1874&auto=format&fit=crop', alt: 'Misty forest path' },
        { src: 'https://images.unsplash.com/photo-1447752875215-b2761acb3c5d?q=80&w=1740&auto=format&fit=crop', alt: 'Sunlight through forest trees' },
        { src: 'https://images.unsplash.com/photo-1501854140801-50d01698950b?q=80&w=1840&auto=format&fit=crop', alt: 'Winding river through green hills' },
        { src: 'https://images.unsplash.com/photo-1472214103451-9374bd1c798e?q=80&w=1740&auto=format&fit=crop', alt: 'Green meadow with a single tree' },
        { src: 'https://images.unsplash.com/photo-1433086966358-54859d0ed716?q=80&w=1887&auto=format&fit=crop', alt: 'Majestic waterfall' },
        { src: 'https://images.unsplash.com/photo-1469474968028-56623f02e42e?q=80&w=1742&auto=format&fit=crop', alt: 'Mountain landscape with a lake' }
    ];

    images.forEach(image => {
        const div = document.createElement('div');
        div.className = 'group relative overflow-hidden rounded-lg shadow-lg cursor-pointer';
        div.innerHTML = `
            <img src="${image.src}" alt="${image.alt}" class="w-full h-full object-cover transform transition-transform duration-500 group-hover:scale-110">
            <div class="absolute inset-0 bg-black bg-opacity-40 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                <p class="text-white text-lg font-semibold">View Image</p>
            </div>
        `;
        div.addEventListener('click', () => openLightbox(image.src, image.alt));
        galleryGrid.appendChild(div);
    });

    const openLightbox = (src, alt) => {
        lightboxImg.src = src;
        lightboxImg.alt = alt;
        lightbox.classList.remove('hidden');
    };

    const closeLightbox = () => {
        lightbox.classList.add('hidden');
        lightboxImg.src = '';
        lightboxImg.alt = '';
    };

    const toggleMobileMenu = () => {
        mobileMenu.classList.toggle('hidden');
    };

    lightboxClose.addEventListener('click', closeLightbox);
    lightbox.addEventListener('click', (e) => {
        if (e.target === lightbox) {
            closeLightbox();
        }
    });
    mobileMenuButton.addEventListener('click', toggleMobileMenu);
});

document.addEventListener('DOMContentLoaded', () => {
    const mobileMenuButton = document.getElementById('mobile-menu-button');
    const mobileMenu = document.getElementById('mobile-menu');

    const toggleMobileMenu = () => {
        mobileMenu.classList.toggle('hidden');
    };

    if (mobileMenuButton) {
        mobileMenuButton.addEventListener('click', toggleMobileMenu);
    }
});
